const secList = document.querySelectorAll(".sec");
const windowHeight = document.documentElement.clientHeight || window.innerHeight;
const handlerScroll = () => {
  secList.forEach((item) => {
    const isLeft = item.classList.contains("left");
    if (isLeft) {
      if (item.getBoundingClientRect().top < windowHeight * 0.5) {
        item.classList.add("active");
      }
      return;
    }
    if (item.getBoundingClientRect().top < windowHeight * 0.7) {
      item.classList.add("active");
    }
  });
};
var isMob = window.innerWidth < 992;
